import React from 'react'

function MenuGenres() {
    const [data, setData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [serverError, setServerError] = useState(null);
    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            fetch("http://filmotheque.e-mingo.net/api/catalog/genre")
            .then((response) => {
                if (response.ok) return response.json();
            })
            .then((data) => {
                setIsLoading(false);
                setData(data);
            })
            .catch((error) => {
                setServerError(error);
                setIsLoading(false);
            });
        };
        fetchData();
        }, []);
  return (
    <li class="nav-item dropdown">
    <NavDropdown title="Genres" id="nav-dropdown">
        <a
            class="nav-link dropdown-toggle"
            href="#"
            role="button"
            data-bs-toggle="dropdown"
            aria-expanded="false"
        >
            Genres
        </a>
        <ul class="dropdown-menu">
            <li><a class="dropdown-item" href="#">Action</a></li>
            <li><a class="dropdown-item" href="#">Aventure</a></li>
            <li><a class="dropdown-item" href="#">Policier</a></li>
            <li><a class="dropdown-item" href="#">Horreur</a></li>
            <li><a class="dropdown-item" href="#">Action</a></li>
            <li><a class="dropdown-item" href="#">Aventure</a></li>
            <li><a class="dropdown-item" href="#">Policier</a></li>
            <li><a class="dropdown-item" href="#">Horreur</a></li>
        </ul>
    </NavDropdown>
    </li>
  )
}

export default MenuGenres
