import React from 'react'

const CarouselImage = (title, poster, backdrop) => {
  return (
    <>
        <img
            class="img-fluid"
            src={backdrop}
            className="d-block w-100"
            alt={title}
        />
        <div class="blur">&nbsp;</div>
        <div class="poster">
            <img
            class="img-fluid"
            src={poster}
            />
        </div>
    </>
  )
}

export default CarouselImage
