import React from 'react'
import { NavDropdown } from 'react-bootstrap'

function DropdownItem(props) {

  let linkTo = '';
  if (type === 'genre') {
    linkTo = `/genre/${name}`;
  } else if (type === 'actor') {
    linkTo = `/actor/${name}`;
  }
  
  return (
    <NavDropdown.Item eventKey={props.id}>
        {props.name}
    </NavDropdown.Item>
  )
}

export default DropdownItem
