import React from "react";
import { Col, Container, Image, Row } from "react-bootstrap";
import { useLoaderData } from "react-router-dom";
import ActorThumbnail from "/ActorThumbnail";
import MoviePost from "./MoviePost";
import { MovieCustomPost } from "./MovieCustomPost";

export default function MovieDetails() {
  const {
    id,
    title,
    tagline,
    release_date,
    overview,
    backdrop_path,
    poster_path,
    credits,
    posts,
  } = useLoaderData("movie");

  return (
    <Container key={id}>
      <Row className="mh-50">
        <Container
          fluid
          className="large rounded first text-white  p-0"
          style={{
            backgroundImage: `url(${backdrop_path})`,
            borderColor: "red",
          }}
        >
          <Container fluid className="custom_bg p-5">
            <Row className="row-cols-2 flex align-items-center justity-items-center">
              <Col className="col-3 text-align-center">
                <Image fluid src={poster_path} />
              </Col>
              <Col className="col-9">
                <h1>
                  {title + "(" + new Date(release_date).getFullYear() + ")"}
                </h1>
                <h5>{tagline}</h5>
                <br />
                <br />
                <h2>Synopsis:</h2>
                {overview}
              </Col>
            </Row>
          </Container>
        </Container>
        <br />
        <br />
        {credits?.cast?.length > 0 && (
          <Container fluid>
            <h2>Acteurs</h2>
            <Row
              xl={6}
              lg={5}
              md={4}
              sm={3}
              xs={2}
              className="g-4 justify-content-center "
            >
              {credits?.cast
                ?.filter((cast) => cast.known_for_department === "Acting")
                .map((actor) => (
                  <ActorThumbnail key={actor.id} data={actor} />
                ))}
            </Row>
          </Container>
        )}
        <Container fluid>
          {posts.length > 0 && (
            <>
              <h2>Avis</h2>
              <Row md={2} xs={1} className="mb-2">
                {posts?.map((post) => (
                  <MoviePost key={post._id} data={post} />
                ))}
              </Row>
            </>
          )}
          <MovieCustomPost
            movie={id}
            author="john.doe@nowhere.com"
            author2="davidjean.mingo@gmail.com"
          />
        </Container>
      </Row>
    </Container>
  );
}
