import React, { useEffect, useState } from 'react';
import ContentBox from './ContentBox';
import { useLoaderData } from 'react-router-dom';

const GenreList = () => {
  const genres = useLoaderData();
  const [genre, setGenre] = useState([]); // Initialisez genre avec un tableau vide
  const [movies, setMovies] = useState([]); // Initialisez movies avec un tableau vide
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`http://filmotheque.e-mingo.net/api/catalog/movie/${genre}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setMovies(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, [genre]);

  console.log(movies);
  return(
    genres && genres.map((genreItem, index) => (
     <ContentBox key={index} title={genreItem} movies={movies} setGenre={setGenre}/> // Filtrer les films par genre
    ))
  )
};

export default GenreList;
