import PropTypes from 'prop-types'
import React, { Component } from 'react'
import { Nav, NavDropdown, Navbar } from 'react-bootstrap'
import { useEffect, useState } from 'react'
import DropdownItem from './DropdownItem'

const GenreNav = () => {
    const [data, setData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [serverError, setServerError] = useState(null);
    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            fetch("http://filmotheque.e-mingo.net/api/catalog/genre")
            .then((response) => {
                if (response.ok) return response.json();
            })
            .then((data) => {
                setIsLoading(false);
                setData(data);
            })
            .catch((error) => {
                setServerError(error);
                setIsLoading(false);
            });
        };
        fetchData();
        }, []);
  return (
    <>
    {isLoading && <span> Données en cours de chargement ...</span>}
    {!isLoading && serverError ? (
    <span>Erreur de chargement des données ! </span>
    ) : (
    data && (
    <Navbar expand="lg" className="bg-body-tertiary">
        <Navbar.Brand href="/genre">Genres</Navbar.Brand>
        {data.map((genre, index) => (
                <Nav.Link href={`/genre/${genre.name}`}>{genre.name}</Nav.Link>
        ))}
    </Navbar>
  ))}
  </>
  )
}

export default GenreNav
