import PropTypes from 'prop-types'
import React, { Component } from 'react'
import { Nav } from 'react-bootstrap'
import { useEffect, useState } from 'react'

export const GenreNav = () => {
    const [data, setData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [serverError, setServerError] = useState(null);
    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            fetch("http://filmotheque.e-mingo.net/api/catalog/genre")
            .then((response) => {
                if (response.ok) return response.json();
            })
            .then((data) => {
                setIsLoading(false);
                setData(data);
            })
            .catch((error) => {
                setServerError(error);
                setIsLoading(false);
            });
        };
        fetchData();
        }, []);
  return (
    <>
    {isLoading && <span> Données en cours de chargement ...</span>}
    {!isLoading && serverError ? (
    <span>Erreur de chargement des données ! </span>
    ) : (
    data && (
    <Nav title="Genres" id="nav-dropdown">
        {data.map((genre, index) => (
            <a href={`/genre/${genre.name}`}>
                <DropdownItem key={index} id={genre.id} name={genre.name} type="genre"/>
            </a>
        ))}
    </Nav>
  ))}
  </>
  )
}

export default GenreNav
