import PropTypes from 'prop-types'
import React, { Component } from 'react'
import { Nav, NavDropdown, Navbar } from 'react-bootstrap'
import { useEffect, useState } from 'react'
import DropdownItem from './DropdownItem'
import { NavLink, Link } from 'react-router-dom';

const GenreNav = () => {
    const [genres, setGenres] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [serverError, setServerError] = useState(null);
    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            fetch("http://filmotheque.e-mingo.net/api/catalog/genre")
            .then((response) => {
                if (response.ok) return response.json();
            })
            .then((genres) => {
                setIsLoading(false);
                setGenres(genres);
            })
            .catch((error) => {
                setServerError(error);
                setIsLoading(false);
            });
        };
        fetchData();
        }, []);
  return (
    <>
    {isLoading && <span> Données en cours de chargement ...</span>}
    {!isLoading && serverError ? (
    <span>Erreur de chargement des données ! </span>
    ) : (
    genres && (
      <nav className="genre-nav">
        {genres.map((genre, index) => (
            <NavLink key={index} to={`/genre/${genre.name}`} className="genre-nav-item">
                {genre.name}
            </NavLink>
        ))}
      </nav>
      )
  )}
  </>
  )
}

export default GenreNav
