import React from 'react'
import { useState, useEffect } from 'react'
import ContentBox from './ContentBox';

export const GenreList = () => {

  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [serverError, setServerError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      fetch("http://filmotheque.e-mingo.net/api/catalog/movie")
      .then((response) => {
          if (response.ok) return response.json();
      })
      .then((data) => {
          setIsLoading(false);
          setData(data);
      })
      .catch((error) => {
          setServerError(error);
          setIsLoading(false);
      });
  };

    fetchData();
  }, []);

  return (
    <></>
    /*<div id="content" className="container">
      {isLoading && <span>Données en cours de chargement...</span>}
      {!isLoading && serverError ? (
        <span>Erreur de chargement des données !</span>
      ) : (
        data.map((movie, index) => (
          <ContentBox key={index} title={movie.genres.name} movies={movie.movies}/>
      )))}
    </div>*/
  );
};

export default GenreList
